DROP TABLE IF EXISTS `payment_bill`;
CREATE TABLE `payment_bill`  (
  `id` bigint(0) NOT NULL,
  `merchant_id` bigint(0) NOT NULL COMMENT '商户id',
  `merchant_name` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '商户名称',
  `merchant_app_id` bigint(0) NOT NULL COMMENT '商户应用Id',
  `merchant_order_no` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '商户订单号',
  `channel_order_no` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '渠道订单号',
  `product_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '商品名称',
  `create_time` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '创建时间',
  `pos_time` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '交易时间',
  `equipment_no` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '终端号',
  `user_account` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '用户账号/标识信息',
  `total_amount` decimal(10, 2) NULL DEFAULT NULL COMMENT '订单金额',
  `trade_amount` decimal(10, 2) NOT NULL COMMENT '实际交易金额',
  `discount_amount` decimal(10, 2) NULL DEFAULT NULL COMMENT '折扣金额',
  `service_fee` decimal(10, 4) NULL DEFAULT NULL COMMENT '手续费',
  `refund_order_no` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '退款单号',
  `refund_money` decimal(10, 2) NULL DEFAULT NULL,
  `platform_channel` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '平台支付渠道',
  `remark` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '备注',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = DYNAMIC;

